def loginPage = 'http://mail.google.com' def username = your mail goes here def password = your password goes here def mailSubject = 'Attach PDF' def mailBody = 'This is an RPA message with attached PDF file.'
timeouts(20 * 1000)
// Login into the mail system open(loginPage) $('#identifierId').val(username).pressEnter() $(byXpath("//*[@name='password']")).val(password) $('#passwordNext').click()
// Populate new message fields $(byText('COMPOSE')).click(); $(By.name('to')).val(username).pressTab() $(by('placeholder', 'Subject')).val(mailSubject).pressTab() $('.editable').val(mailBody).pressEnter()
// Start attaching file. $(byXpath("//div[@class='a1 aaA aMZ']")).click()
